/* ---------- CART UTILITIES (localStorage) ---------- */
const CART_KEY = "ecom_cart_v1";
function getCart(){
  try {
    return JSON.parse(localStorage.getItem(CART_KEY)) || [];
  } catch(e){ return [];}
}
function saveCart(cart){
  localStorage.setItem(CART_KEY, JSON.stringify(cart));
}
function addToCart(prodId, qty=1){
  const cart = getCart();
  const idx = cart.findIndex(i=>i.id===prodId);
  if(idx>-1){
    cart[idx].qty += qty;
  } else {
    cart.push({id:prodId, qty});
  }
  saveCart(cart);
}

/* format currency */
function formatPrice(n){
  return n.toLocaleString(undefined, {style:'currency',currency:'USD'});
}

/* ---------- DOM Helpers used across pages ----------*/
function renderCartCount(){
  const cart = getCart();
  const count = cart.reduce((s,i)=>s+i.qty,0);
  const el = document.querySelectorAll(".cart-count");
  el.forEach(x => x.textContent = count);
}

/* On product cards - add listeners that navigate to product page */
function enableProductButtons(){
  document.querySelectorAll(".buy-now").forEach(btn=>{
    btn.addEventListener("click", (e)=>{
      const id = btn.dataset.id;
      addToCart(id,1);
      // direct to cart
      window.location.href = "cart.html";
    });
  });
  document.querySelectorAll(".view-product").forEach(btn=>{
    btn.addEventListener("click", (e)=>{
      const id = btn.dataset.id;
      window.location.href = `product.html?id=${id}`;
    });
  });
  document.querySelectorAll(".add-cart").forEach(btn=>{
    btn.addEventListener("click", (e)=>{
      const id = btn.dataset.id;
      addToCart(id,1);
      renderCartCount();
      btn.textContent = "Added ✓";
      setTimeout(()=> btn.textContent = "Add to Cart", 900);
    });
  });
}

/* ---------- PAGE-SPECIFIC RENDERERS ---------- */
function renderTrending(whereSelector = ".trending-grid"){
  const wrap = document.querySelector(whereSelector);
  if(!wrap) return;
  // show first 6 as trending
  const items = PRODUCTS.slice(0,6);
  wrap.innerHTML = items.map(p=>`
    <div class="card">
      <div class="imgwrap"><img src="${p.image}" alt="${p.name}"></div>
      <h3>${p.name}</h3>
      <div class="meta">
        <div class="price">${formatPrice(p.price)}</div>
        <div style="display:flex;gap:8px">
          <button class="btn btn-ghost view-product" data-id="${p.id}">View</button>
          <button class="btn btn-primary add-cart" data-id="${p.id}">Add to Cart</button>
        </div>
      </div>
    </div>
  `).join("");
  enableProductButtons();
  renderCartCount();
}

function renderShop(filters = null){
  const grid = document.querySelector(".shop-grid");
  if(!grid) return;
  const items = filters ? PRODUCTS.filter(p=> filters.includes(p.category)) : PRODUCTS;
  grid.innerHTML = items.map(p=>`
    <div class="card">
      <div class="imgwrap"><img src="${p.image}" alt="${p.name}"></div>
      <h3>${p.name}</h3>
      <div class="meta">
        <div class="price">${formatPrice(p.price)}</div>
        <div style="display:flex;gap:8px">
          <button class="btn btn-ghost view-product" data-id="${p.id}">Details</button>
          <button class="btn btn-primary add-cart" data-id="${p.id}">Add to Cart</button>
        </div>
      </div>
    </div>
  `).join("");
  enableProductButtons();
  renderCartCount();
}

/* product detail page loader */
function loadProductDetail(){
  const params = new URLSearchParams(location.search);
  const id = params.get("id");
  if(!id) return;
  const p = PRODUCTS.find(x=>x.id===id);
  if(!p) return;
  const container = document.querySelector(".product-detail-wrap");
  if(!container) return;
  container.innerHTML = `
    <div class="product-detail container">
      <div class="bigimg">
        <img src="${p.image}" alt="${p.name}">
      </div>
      <div>
        <h1 style="margin-top:0">${p.name}</h1>
        <p class="small" style="color:var(--muted);margin-bottom:14px">${p.category}</p>
        <div style="font-size:20px;font-weight:700;margin-bottom:14px">${formatPrice(p.price)}</div>
        <p style="color:var(--muted);margin-bottom:18px">${p.description}</p>
        <div style="display:flex;gap:12px;align-items:center">
          <input type="number" id="qty" min="1" value="1" style="width:80px;padding:8px;border-radius:8px;border:1px solid #e6eef2">
          <button id="addNow" class="btn btn-primary">Add to Cart</button>
          <button id="buyNow" class="btn btn-ghost">Buy Now</button>
        </div>
      </div>
    </div>
  `;
  document.getElementById("addNow").addEventListener("click", ()=>{
    const q = parseInt(document.getElementById("qty").value || "1",10);
    addToCart(p.id, q);
    renderCartCount();
    alert("Added to cart");
  });
  document.getElementById("buyNow").addEventListener("click", ()=>{
    const q = parseInt(document.getElementById("qty").value || "1",10);
    addToCart(p.id,q);
    window.location.href = "cart.html";
  });
}

/* cart page rendering */
function renderCartPage(){
  const container = document.querySelector(".cart-wrap");
  if(!container) return;
  const cart = getCart();
  if(cart.length===0){
    container.innerHTML = `
      <div style="padding:30px;background:var(--card);border-radius:12px;text-align:center">
        <h3>Your cart is empty</h3>
        <p class="small">Add items from shop to get started.</p>
        <a class="btn btn-primary" href="shop.html" style="margin-top:12px;display:inline-block">Go to Shop</a>
      </div>
    `;
    renderCartCount();
    return;
  }
  let subtotal = 0;
  const rows = cart.map(item=>{
    const p = PRODUCTS.find(x=>x.id===item.id) || {};
    const total = (p.price||0) * item.qty;
    subtotal += total;
    return `
      <tr data-id="${item.id}">
        <td style="width:120px"><img src="${p.image}" style="width:100px;height:80px;object-fit:cover;border-radius:8px"></td>
        <td><strong>${p.name||'Unknown'}</strong><div class="small">${p.category||''}</div></td>
        <td>${formatPrice(p.price||0)}</td>
        <td><input type="number" class="qty-input" value="${item.qty}" min="1" data-id="${item.id}"></td>
        <td>${formatPrice(total)}</td>
        <td><button class="btn btn-ghost remove-item" data-id="${item.id}">Remove</button></td>
      </tr>
    `;
  }).join("");
  container.innerHTML = `
    <div style="overflow:auto">
      <table class="cart-table" style="min-width:760px">
        <thead style="background:var(--bg)"><tr><th>Product</th><th>Details</th><th>Price</th><th>Qty</th><th>Total</th><th></th></tr></thead>
        <tbody>${rows}</tbody>
      </table>
    </div>
    <div style="margin-top:18px;display:flex;justify-content:flex-end;gap:16px;align-items:center">
      <div style="text-align:right">
        <div class="small">Subtotal</div>
        <div style="font-weight:700;font-size:18px">${formatPrice(subtotal)}</div>
        <div class="small" style="color:var(--muted)">Shipping calculated at checkout</div>
        <div style="margin-top:12px;display:flex;gap:10px;justify-content:flex-end">
          <button id="clearCart" class="btn btn-ghost">Clear Cart</button>
          <button id="checkout" class="btn btn-primary">Checkout</button>
        </div>
      </div>
    </div>
  `;
  // qty listeners
  document.querySelectorAll(".qty-input").forEach(inp=>{
    inp.addEventListener("change", (e)=>{
      const id = inp.dataset.id;
      let v = parseInt(inp.value || '1',10); if(v<1) v=1; inp.value=v;
      const cart = getCart();
      const idx = cart.findIndex(i=>i.id===id);
      if(idx>-1){ cart[idx].qty = v; saveCart(cart); renderCartPage(); renderCartCount(); }
    });
  });
  // remove listeners
  document.querySelectorAll(".remove-item").forEach(btn=>{
    btn.addEventListener("click", ()=>{
      const id = btn.dataset.id;
      let cart = getCart();
      cart = cart.filter(i=>i.id!==id);
      saveCart(cart);
      renderCartPage(); renderCartCount();
    });
  });
  document.getElementById("clearCart").addEventListener("click", ()=>{
    if(!confirm("Clear entire cart?")) return;
    saveCart([]); renderCartPage(); renderCartCount();
  });
  document.getElementById("checkout").addEventListener("click", ()=>{
    alert("Checkout flow placeholder — implement payment gateway here.");
  });
  renderCartCount();
}

/* initialize general UI bits */
document.addEventListener("DOMContentLoaded", ()=>{
  // render navbar cart count
  renderCartCount();

  // try render sections if present
  renderTrending(".trending-grid");
  renderShop(); // shop page will re-render itself if needed
  loadProductDetail(); // product page
  renderCartPage(); // cart page

  // Category ring buttons behavior (if present)
  document.querySelectorAll(".ring").forEach(r=>{
    r.addEventListener("click", ()=>{
      const cats = r.dataset.cat ? [r.dataset.cat] : null;
      renderShop(cats);
      // scroll to products
      const el = document.querySelector(".shop-grid");
      if(el) el.scrollIntoView({behavior:'smooth', block:'center'});
    });
  });

  renderCartCount();
});

